mapper.update:
update student
name = #{name}, no = #{no}, clazz_id = #{clazzId}, gender = #{gender}, phone = #{phone}, id_card = #{idCard}, is_college = #{isCollege}, degree = #{degree}, address = #{address}, graduation_Date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime}
where id = #{id}

update student
set violation_count = violation_count + 1,
violation_score = violation_score + #{score}
where id = #{id}

mapper.select:
select s.*, c.name as clazzName from student s left join clazz c on s.clazz_id = c.id
s.name like concat('%',#{name},'%')
and s.degree = #{degree}
and s.clazz_id = #{classId}
order by c.update_time desc
